import { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [trainingStatus, setTrainingStatus] = useState('Not Started');

  interface GameState {
    message: string;
    board: string[];
  }

  const [gameState, setGameState] = useState<GameState | null>(null);
  const [currentPlayer, setCurrentPlayer] = useState<string | null>('X');
  const [board, setBoard] = useState(Array(9).fill(null));

  const startTraining = async () => {
    try {
      setTrainingStatus('In Progress...');
      await axios.post('http://localhost:5000/train');
      setTrainingStatus('Completed');
    } catch (error) {
      console.error('Error starting training:', error);
      setTrainingStatus('Error');
    }
  };

  const makeMove = async (index: number) => {
    if (board[index] || trainingStatus !== 'Completed' || currentPlayer !== 'X') return;

    const newBoard = [...board];
    newBoard[index] = 'X';
    setBoard(newBoard);

    try {
      const response = await axios.post('http://localhost:5000/simulate', { board: newBoard });
      const { board: updatedBoard, message } = response.data;

      setBoard(updatedBoard);
      setGameState({ message, board: updatedBoard });

      if (!message.includes('continua')) {
        setCurrentPlayer(null);
      } else {
        setCurrentPlayer('X');
      }
    } catch (error) {
      console.error('Error simulating move:', error);
    }
  };

  const renderSquare = (index: number) => {
    return (
      <button
        className="square"
        onClick={() => makeMove(index)}
        style={{
          width: '60px',
          height: '60px',
          display: 'inline-block',
          margin: '5px',
          fontSize: '24px',
          textAlign: 'center',
          lineHeight: '60px',
          cursor: trainingStatus === 'Completed' ? 'pointer' : 'not-allowed',
          backgroundColor: trainingStatus === 'Completed' ? '#fff' : '#ccc',
        }}
        disabled={!!board[index] || trainingStatus !== 'Completed'}
      >
        {board[index]}
      </button>
    );
  };

  return (
    <div className="container mt-5">
      <h1>Neural Network Training and Game</h1>

      <div className="mb-4">
        <h2>Train Neural Network</h2>
        <button className="btn btn-primary" onClick={startTraining}>
          Start Training
        </button>
        <p>Status: {trainingStatus}</p>
      </div>

      <div>
        <h2>Game</h2>
        <div style={{ display: 'inline-block', margin: '10px' }}>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '5px' }}>
            {Array.from({ length: 9 }).map((_, index) => renderSquare(index))}
          </div>
          {gameState && <p>{gameState.message}</p>}
        </div>
        {trainingStatus !== 'Completed' && (
          <p style={{ color: 'red' }}>Please train the neural network before playing.</p>
        )}
      </div>
    </div>
  );
};

export default App;
